div.script:
	$("body").ready( function() {
$(".mt-tabs li:not(.selected) a").click( function() { 
var contenthref = $(this).attr("href");
if (contenthref=='#') {
var contentid = $(this).attr("title");
$(".mt-tabs li a").removeClass("selected");
$(this).addClass("selected");

$(".mt-content > div:not(.hide)").fadeOut( function() {
	$(".mt-content > div." + contentid).fadeIn();
	
	$(".mt-content > div").addClass("hide");
	$(".mt-content > div." + contentid).removeClass("hide");
});
return false;
}

});

$(".mt-tabs li:not(.title):last a").addClass("selected");

$(".mt-tips span:first").addClass("selected");	
$(".mt-tips span:last").attr("pos","last");	

$(".mt-tips span.selected").fadeIn();	

setInterval ( "nextTip()", 6000 );
	});
	
	function nextTip() {
	
	
$(".mt-tips span.selected").each( function() {
$(this).hide();
$(this).removeClass("selected");
var pos = $(this).attr("pos");

if (pos=="last") {
	$(this).parent().find("span:first").addClass("selected");
	$(this).parent().find("sp an:first").show();
} else {
	$(this).find("+ span").addClass("selected");
	$(this).find("+ span").show();
}
});

	}
